mapper.select:
select no, imgurl, title, categoryid, userid, date, likes, comments
from DIARY
order by ${param1} desc limit #{param2}, 6;

select no, imgurl, title, categoryid, userid, date, likes, comments
from DIARY where categoryid = #{param2}
order by ${param3} desc limit #{param1}, 6;

select no, imgurl, title, categoryid, userid, date, likes, comments
from DIARY where userid like CONCAT('%',#{param3},'%') OR title like CONCAT('%',#{param3},'%')
    order by ${param2} desc limit #{param1}, 6;

select no, imgurl, title, categoryid, userid, date,
    (select count(*) from LIKES where d_no = no) as likes,
    (select count(*) from COMMENTS where d_no = no) as comments from DIARY
where userid = #{param1}
order by date desc limit #{param2}, 5;
